    echo '```' >> "$REPORT_FILE"
    echo "" >> "$REPORT_FILE"
    echo "Exit code: $exit_code" >> "$REPORT_FILE"
    echo "</details>" >> "$REPORT_FILE"

    return $exit_code
}

